# Documento Php<?php# Host$host = "seu_host";# User$user = "seu_user";# Password$password = "seu_password";# DB name$db = "seu_db";# Varíavel de conexão passando os parãmetros$con = mysqli_connect($host,$user,$password,$db);# Métodos Post$param_name = $_POST['param_name']; # Parãmetro# Verificando apenas um campo, no caso o parãmetro para ser verificado$sql = $con->query("SELECT * FROM Sua_tabela WHERE param_name ='$param_name'");# Se a varíavel sql conter algo maior que 0if(mysqli_num_rows($sql) > 0){# Retornar valor Falseecho "Retornar valor falso";# Finalizar comandoexit();# Caso o contrário} else { # Se a conexão der certo, inserir os dados do método post if(!$con->query("insert into Sua_tabela (param_name) values ('$param_name')")) die ('Mensagem de erro');# Retornar valor Trueecho "Retornar valor verdadeiro";}# Fim do documento